import React from 'react';
import { Table, Input, Modal, List, Button, Radio } from 'antd';

var storedData = [];
var issuerArr = ['a', 'b', 'c', 'd', 'e', 'f', 'g'];
var titleArr = ['title1', 'title2', 'title3', 'title4','title5', 'title6'];
var explanationArr = ['explanation1', 'explanation2','explanation3','explanation4', 'explanation5','explanation6'];
var registerdate;

const Search = Input.Search;

const columns = [{
                  title: 'TopicID',
                  dataIndex: 'topicID',
                  key: 'topicID',
                  sorter: (a, b) => a.topicID.length - b.topicID.length,
                  width: '10%',
                }, {
                  title: 'Issuer',
                  dataIndex: 'issuer',
                  key: 'issuer',
                  width: '10%',
                }, {
                  title: 'Title',
                  dataIndex: 'title',
                  key: 'title',
                  width: '15%',

                }, {
                  title: 'Explanation',
                  dataIndex: 'explanation',
                  key: 'explanation',
                  width: '30%',
                }, {
                  title: 'Registered on',
                  dataIndex: 'registerDate',
                  key: 'registerDate',
                }];

  const listData = [
    'Racing car sprays burning fuel into crowd.',
    'Japanese princess to wed commoner.',
    'Australian walks 100km after outback crash.',
    'Man charged over missing wedding girl.',
    'Los Angeles battles huge wildfires.',
  ];

  class Topic extends React.Component {
  state = {
    data: [],
    modalVisible: false,
    topicID: '',
    issuer: '',
    title: '',
    explanation: '',
    registerDate: '',
  }

  constructor(props) {
    super(props);
  }

  showModal = (record) => {
    this.setState({
      modalVisible: true,
      topicID: record.topicID,
      issuer: record.issuer,
      title: record.title,
      explanation: record.explanation,
      registerDate: record.registerDate,
    });
  }

  handleOk = (e) => {
    console.log(e);
    this.setState({
      modalVisible: false,
    });
  }

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      modalVisible: false,
    });
  }

  componentWillMount() {
    this.initialize();
  }

  initialize() {
    var i;
    for(i=0; i<20; i++) {
      //Get data (hardcoding)
      storedData.push({
        topicID: Math.floor((Math.random() * 2000)+1),
        issuer: issuerArr[Math.floor(Math.random() * 6)],
        title: titleArr[Math.floor((Math.random() * 6))],
        explanation: explanationArr[Math.floor(Math.random() * 6)],
        registerDate: Date.now() - Math.floor((Math.random()*10)),
      });
    }
    this.setState({data: storedData});
  }

  handleSorting = (e) => {
    var sortData=[];
    console.log('hanle sorting value: ',e.target);

    switch(e.target.value) {
      case '1' :
        //All topic
        this.setState({data: storedData});
      break;
      case '2' :
        //Pre-fixed topic (1 ~ 1024)
        storedData.forEach(function(element) {
          if(Object.values(element)[0]<1025) {
            sortData.push(element);
          }
        });
        this.setState({data: sortData});
      break;
      case '3' :
        //Added topic (1025 ~)
        storedData.forEach(function(element) {
          if(Object.values(element)[0]>1024) {
            sortData.push(element);
          }
        });
        this.setState({data: sortData});
      break;
    }
  }

  onSearch(value) {
    console.log('onSearch value: ',value);
    if (value == '' || value == undefined) {
      this.setState({data: storedData});
    }
    else {
      var searchData = [];
      storedData.forEach(function(element) {
        //Exist value
        if(Object.values(element).indexOf(value) > -1) {
          console.log('onSearch1: ',Object.values(element).indexOf(value));
          searchData.push(element);
        }
      });
      this.setState({data: searchData});
    }
  }

  render() {
    return (
      <div>
        <div>
        <Button type="primary" size='large'>Add New Topic</Button>
        <Search
          placeholder="Search by Creator, No., Keyword"
          onSearch={value => this.onSearch(value)}
          enterButton
          style={{ width: 500, float: 'right', marginBottom: '20px' }}
        />
        </div>
        <Radio.Group style={{margin: '10px 10px 0 0'}} onChange={this.handleSorting}>
          <Radio.Button value='1'>All</Radio.Button>
          <Radio.Button value='2'>Pre-fixed</Radio.Button>
          <Radio.Button value='3'>Added</Radio.Button>
        </Radio.Group>
        <br />
        <Modal
          style={{ textAlign:'center' }}
          width={900}
          title={this.state.title}
          visible={this.state.modalVisible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}>
          <div>
            <h5 style={{ float: 'right' }}>Registered on: {this.state.registerDate}</h5>
            <h3 style={{ margin: '10px 0 0 0', float: 'left'}}>{this.state.explanation}</h3>
            <h3 style={{ margin: '10px 0', float: 'left' }}>Creator: {this.state.issuer} / 0x7304f14b0909640acc4f6a192381091eb1f37701</h3>
            <List
              size='small'
              header={<div><h2>Topic Created</h2></div>}
              bordered
              dataSource={listData}
              renderItem={item => (<List.Item>{item}</List.Item>)}
            />
            <br />
          </div>
        </Modal>
        <br />
        <Table
          rowKey={record => record.uid}
          onRow={(record, index) => ({
            onClick: () => { console.log('onRow', record); this.showModal(record); } 
          })}
          columns={columns}
          dataSource={this.state.data}
        />
      </div>
    );
  }
}

export {Topic};
