import React from 'react';
import { Input, Collapse } from 'antd';
import faqContents from './faqContent';

class FAQ extends React.Component {
  data = {
    children: [],
  }

  state = {
    isSearch : false,
  }

  constructor(props) {
    super(props);
  }

  onSearch(value) {
    /*if (value == '' || value == undefined) {
      this.data['infoData'] = storedData;
    } else {
      var searchData = [];
      storedData.forEach(function(element) {
        //Exist value
        Object.values(element).forEach(function(val) {
          if(val.toLowerCase().includes(value.toLowerCase()))
            searchData.push(element);
        });
      });
      this.data['infoData']=searchData;
    }
    this.setState({isSearch: true});*/
  }

  onSearchInputChange = (e) => {
    this.onSearch(e.target.value);
    this.getCollapseComponent('search', e.target.value);
    this.setState({isSearch: true});
  }

  getCollapseComponent(type, value) {
    console.log('getCollapseComponent: ',type, value);
    for (var i=0; i < faqContents.length; i++) {

      if(type == 'init') {
        this.data['children'].push(
          <Collapse.Panel header={faqContents[i].title} key={i}>
            <p>{faqContents[i].content}</p>
          </Collapse.Panel>
        );
      } else {
        if(faqContents[i].title.includes(value) || faqContents[i].contents.includes(value)) {
          this.data['children'].push(
            <Collapse.Panel header={faqContents[i].title} key={i}>
              <p>{faqContents[i].content}</p>
            </Collapse.Panel>
          );
        }
      }
    }
    console.log('getCollapseComponent: ',this.data['children']);
    return this.data['children'];
  }

  render() {
    return (
      <div>
        <h1><b>FAQ</b></h1>
        <br />
        <Input.Search
          placeholder='Search by Topic, Keywords or Phrase'
          onChange={this.onSearchInputChange}
          onSearch={value => this.onSearch(value)}
          enterButton
          style={{ width: '80%', marginBottom: '20px' }}
        />
        <Collapse accordion
          style={{margin: '20px 0' }}>
            {this.getCollapseComponent('init','')}
        </Collapse>
      </div>
    );
  }
}

export {FAQ};
